import styled from "styled-components";
import axios from "axios";
import {useState} from "react";
import {useDispatch, useSelector} from "react-redux";

const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(
      rgba(255, 255, 255, 0.5),
      rgba(255, 255, 255, 0.5)
    ),

      center;
  background-size: cover;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Wrapper = styled.div`
  width: 40%;
  padding: 20px;
  background-color: white;
`;

const Title = styled.h1`
  font-size: 24px;
  font-weight: 300;
`;

const Form = styled.form`
  display: flex;
  flex-wrap: wrap;
`;

const Input = styled.input`
  flex: 1;
  min-width: 40%;
  margin: 20px 10px 0px 0px;
  padding: 10px;
`;

const Button = styled.button`
  width: 40%;
  border: none;
  padding: 15px 20px;
  padding-top: 5px;
  background-color: teal;
  color: white;
  cursor: pointer;
`;

const Register = () => {
    const [firstname, setFirstname] = useState("");
    const [lastname, setLastname] = useState("");
    const [street, setStreet] = useState("");
    const [number, setNumber] = useState("");
    const [postalcode, setPostalcode] = useState("");
    const [city, setCity] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const dispatch = useDispatch();
    const {isFetching, error } = useSelector((state)=>state.order);

    const handleClick = (e) => {
        e.preventDefault();
        order(dispatch, {firstname,lastname,street,number,postalcode,city,email,phone});
        console.log(order);
    }

    return (
        <Container>
            <Wrapper>
                <Title>Checkout</Title>
                <Form>
                    <Input placeholder="firstname" onChange={(e) => setFirstname(e.target.value)}/>
                    <Input placeholder="lastname" onChange={(e) => setLastname(e.target.value)}/>
                    <Input placeholder="street" onChange={(e) => setStreet(e.target.value)}/>
                    <Input placeholder="number" onChange={(e) => setNumber(e.target.value)}/>
                    <Input placeholder="postalcode" onChange={(e) => setPostalcode(e.target.value)} />
                    <Input placeholder="city" onChange={(e) => setCity(e.target.value)}/>
                    <Input placeholder="email" onChange={(e) => setEmail(e.target.value)}/>
                    <Input placeholder="phone" onChange={(e) => setPhone(e.target.value)}/>
                    <Button>Checkout</Button>
                </Form>
            </Wrapper>
        </Container>
    );
};

export default Register;
