import styled from "styled-components";
import axios from "axios";
import {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {publicRequest} from "../requestMethods";

const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(
      rgba(255, 255, 255, 0.5),
      rgba(255, 255, 255, 0.5)
    ),

      center;
  background-size: cover;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Wrapper = styled.div`
  width: 40%;
  padding: 20px;
  background-color: white;
`;

const Title = styled.h1`
  font-size: 24px;
  font-weight: 300;
`;

const Form = styled.form`
  display: flex;
  flex-wrap: wrap;
`;

const Input = styled.input`
  flex: 1;
  min-width: 40%;
  margin: 20px 10px 0px 0px;
  padding: 10px;
`;

const Button = styled.button`
  width: 40%;
  border: none;
  padding: 15px 20px;
  padding-top: 5px;
  background-color: teal;
  color: white;
  cursor: pointer;
`;

const Register = () => {

    const [data, setData] = useState({
        firstname:"",
        lastname:"",
        street:"",
        number:"",
        postalcode:"",
        city:"",
        email:"",
        phone:"",
    })
    const handleClick = (e) => {
       const newData = {...data};
       newData[e.target.id] = e.target.value;
       setData(newData);

        console.log(newData);
    }
    useEffect(() => {
        const postOrder = async () => {
            try {
                const res = await publicRequest.post("/orders/"+id);
                postOrder(res.data);
            } catch {}
        };
        console.log(id);
        postOrder();
    }, [id]);

    return (
        <Container>
            <Wrapper>
                <Title>Checkout</Title>
                <Form>
                    <Input placeholder="firstname" onChange={(e) => handleClick(e)} id="firstname" value={data.firstname}/>
                    <Input placeholder="lastname" onChange={(e) => handleClick(e)} id="lastname" value={data.lastname}/>
                    <Input placeholder="street" onChange={(e) => handleClick(e)} id="street" value={data.street}/>
                    <Input placeholder="number" onChange={(e) => handleClick(e)} id="number" value={data.number}/>
                    <Input placeholder="postalcode" onChange={(e) => handleClick(e)} id="postalcode" value={data.postalcode} />
                    <Input placeholder="city" onChange={(e) => handleClick(e)} id="city" value={data.city}/>
                    <Input placeholder="email" onChange={(e) => handleClick(e)} id="email" value={data.email}/>
                    <Input placeholder="phone" onChange={(e) => handleClick(e)} id="phone" value={data.phone}/>
                    <Button>Checkout</Button>
                </Form>
            </Wrapper>
        </Container>
    );
};

export default Register;
