import styled from "styled-components";
import axios from "axios";
import {useState} from "react";

const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(
      rgba(255, 255, 255, 0.5),
      rgba(255, 255, 255, 0.5)
    ),

      center;
  background-size: cover;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Wrapper = styled.div`
  width: 40%;
  padding: 20px;
  background-color: white;
`;

const Title = styled.h1`
  font-size: 24px;
  font-weight: 300;
`;

const Form = styled.form`
  display: flex;
  flex-wrap: wrap;
`;

const Input = styled.input`
  flex: 1;
  min-width: 40%;
  margin: 20px 10px 0px 0px;
  padding: 10px;
`;

const Button = styled.button`
  width: 40%;
  border: none;
  padding: 15px 20px;
  padding-top: 5px;
  background-color: teal;
  color: white;
  cursor: pointer;
`;

const Register = () => {
    const [data, setData] =useState({
        firstname: "",
        lastname: "",
        street: "",
        number: "",
        postalcode: "",
        city: "",
        email: "",
        phone: ""
    })
    function handle(e){
        const newData ={...data}
        newData[e.target.id] = e.target.value
        setData(newData)
        console.log(newData)
    }


    return (
        <Container>
            <Wrapper>
                <Title>Checkout</Title>
                <Form>
                    <Input onChange={(e)=>handle(e)} id="firstname" value={data.firstname} placeholder="firstname" />
                    <Input onChange={(e)=>handle(e)} id="lastname" value={data.lastname} placeholder="lastname" />
                    <Input onChange={(e)=>handle(e)} id="street" value={data.street} placeholder="street" />
                    <Input onChange={(e)=>handle(e)} id="number" value={data.number} placeholder="number" />
                    <Input onChange={(e)=>handle(e)} id="postalcode" value={data.postalcode} placeholder="postalcode" />
                    <Input onChange={(e)=>handle(e)} id="city" value={data.city} placeholder="city" />
                    <Input onChange={(e)=>handle(e)} id="email" value={data.email} placeholder="email" />
                    <Input onChange={(e)=>handle(e)} id="phone" value={data.phone} placeholder="phone" />
                    <Button>Checkout</Button>
                </Form>
            </Wrapper>
        </Container>
    );
};

export default Register;
